$(document).ready(function () {
    function e() {
      $("#withOverpayment").is(":checked")
        ? $(".installment__group--deposit").addClass("active")
        : ($(".installment__group--deposit").removeClass("active"),
          $("#deposit").val("0"))
    }
    function t() {
      var e = parseInt($("#budget").val()),
        t = parseInt($('input[name="time"]:checked').val()),
        a = 0,
        n = parseInt($("#deposit").val()),
        d = 3e4,
        o = 5e5
      if (
        ($("#budget").removeClass("error"),
        $("#deposit").removeClass("error"),
        e >= d && o >= e)
      ) {
        e >= n
          ? (a = ((e - n) / t).toFixed())
          : ($("#deposit").addClass("error"), (a = (e / t).toFixed()))
        var r = a.toString().replace(/(\d)(?=(\d\d\d)+([^\d]|$))/g, "$1 "),
          i = e.toString().replace(/(\d)(?=(\d\d\d)+([^\d]|$))/g, "$1 ")
        $("#payment").html(r), $("#amount").html(i)
      } else $("#budget").addClass("error")
    }
    $("#installmentForm").on("click", function () {
      e()
    }),
      $("#installmentForm").on("change", function () {
        t()
      })
  })